options.apply_js:
var utils = window.optimizely.get('utils');

// helper functions from developer documentation: https://developers.optimizely.com/classic/javascript/code-samples/index.html
var setCookie = function(c_name,value,exdays,c_domain) { 
  c_domain = (typeof c_domain === "undefined") ? "" : "domain=" + c_domain + ";"; 
  var exdate=new Date(); exdate.setDate(exdate.getDate() + exdays); 
  var c_value=escape(value) + ((exdays==null) ? "" : "; expires="+exdate.toUTCString()); 
  document.cookie=c_name + "=" + c_value + ";" + c_domain + "path=/"; 
};

 var getCookie = function(name) {
   var match = document.cookie.match(name+'=([^;]*)');
   return match ? match[1] : undefined;
 };

utils.waitForElement('body').then(function(element){
  var html = widget.$html;
  var cookieName = 'butterbar_' + widget.$id + '_dismissed';
  var cookiePresent = getCookie(cookieName);

  // check to make sure cookie is undefined
  if (!cookiePresent) {
    element.insertAdjacentHTML('afterbegin',html);

    // Handle close action 
    utils.waitUntil(function() {
      var closerOnPage = document.querySelectorAll('#opti-banner-close');
      return closerOnPage.length > 0;
    }).then(function() {
        document.getElementById("opti-banner-close").addEventListener('click', function (event) {
          document.getElementsByClassName('opti-banner')[0].classList.add('opti-hide-banner');
          document.getElementsByTagName('body')[0].classList.add('opti-body-no-margin');
          setCookie(cookieName, 'true', 1);
        });
    });
  }

});

options.css:
.opti-banner {
  color: white;
  padding: 10px;
	position: fixed;
  top: 0px;
  z-index: 100000000;
  width: 100%;
  text-align: center;
  height: 45px;
}

.opti-link {
	text-decoration: underline;
  color: white;
}

body {
 margin-top: 45px; 
}

#opti-banner-close {
 float: right; 
 text-decoration: none;
}

.opti-hide-banner {
  display:none;
  height: 0px;
}

.opti-body-no-margin {
 margin-top: 0; 
}
